import React, { useState, useEffect } from 'react';
import BlogCard from '../components/BlogCard';
import api from '../utils/api';
import { toast } from 'react-toastify';

function MyBlogs() {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const fetchMyBlogs = async () => {
      try {
        const res = await api.get('/blogs/my-blogs');
        setBlogs(res.data.data);
      } catch (err) {
        toast.error(err.response?.data?.message || 'Failed to fetch blogs');
      }
    };
    fetchMyBlogs();
  }, []);

  const handleDelete = (blogId) => {
    setBlogs(blogs.filter((blog) => blog._id !== blogId));
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">My Blogs</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {blogs.map((blog) => (
          <BlogCard key={blog._id} blog={blog} isMyBlog={true} onDelete={handleDelete} />
        ))}
      </div>
    </div>
  );
}

export default MyBlogs;
