import React, { useState } from 'react';
import { Menu, X, ChevronDown, BookOpen, User, PenTool, LogOut } from 'lucide-react';

// This component demonstrates the professional navbar
// In a real application, you'd connect it to your AuthContext and react-router
function Navbar() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true); // For demo purposes
  
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };
  
  // For demo purposes only
  const handleLogout = () => {
    setIsLoggedIn(false);
    setTimeout(() => setIsLoggedIn(true), 2000);
  };
  
  return (
    <div className="font-sans">
      <nav className="bg-gradient-to-r from-blue-800 to-blue-900 shadow-lg fixed w-full z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo and Brand */}
            <div className="flex-shrink-0 flex items-center">
              <BookOpen className="h-8 w-8 text-white" />
              <a href="#" className="ml-2 text-2xl font-extrabold tracking-tight text-white">
                <span className="text-blue-300">Quill</span>
                <span>Press</span>
              </a>
            </div>
            
            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-1">
              {isLoggedIn ? (
                <>
                  <NavItem href="/blogs" current={true}>
                    Explore
                  </NavItem>
                  <NavItem href="/my-blogs">
                    My Articles
                  </NavItem>
                  
                  {/* Dropdown */}
                  <div className="relative ml-3 group">
                    <button className="flex items-center px-3 py-2 text-sm font-medium text-blue-100 rounded-md hover:bg-blue-700 hover:text-white transition-colors duration-200">
                      <span>Create</span>
                      <ChevronDown className="ml-1 h-4 w-4" />
                    </button>
                    
                    <div className="absolute right-0 w-48 mt-2 origin-top-right bg-white rounded-md shadow-lg opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200 z-50">
                      <div className="py-1">
                        <a href="/create-blog" className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 hover:text-blue-700">
                          <PenTool className="mr-2 h-4 w-4" />
                          New Article
                        </a>
                        <a href="/create-series" className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-blue-50 hover:text-blue-700">
                          <BookOpen className="mr-2 h-4 w-4" />
                          New Series
                        </a>
                      </div>
                    </div>
                  </div>
                  
                  {/* User Menu */}
                  <div className="ml-4 relative flex-shrink-0">
                    <div>
                      <button className="flex text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-1 focus:ring-blue-500">
                        <div className="h-8 w-8 rounded-full bg-blue-200 flex items-center justify-center text-blue-800 font-bold">
                          JD
                        </div>
                      </button>
                    </div>
                  </div>
                  
                  <button
                    onClick={handleLogout}
                    className="ml-4 flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-200"
                  >
                    <LogOut className="mr-2 h-4 w-4" />
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <a href="/login" className="px-4 py-2 text-sm font-medium text-blue-100 hover:text-white transition-colors duration-200">
                    Login
                  </a>
                  <a 
                    href="/signup"
                    className="ml-2 px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-all duration-200"
                  >
                    Sign Up
                  </a>
                </>
              )}
            </div>
            
            {/* Mobile menu button */}
            <div className="md:hidden flex items-center">
              <button
                onClick={toggleMobileMenu}
                className="p-2 rounded-md text-blue-100 hover:text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white"
              >
                {isMobileMenuOpen ? (
                  <X className="h-6 w-6" />
                ) : (
                  <Menu className="h-6 w-6" />
                )}
              </button>
            </div>
          </div>
        </div>

        {/* Mobile menu, show/hide based on menu state */}
        {isMobileMenuOpen && (
          <div className="md:hidden bg-blue-800">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              {isLoggedIn ? (
                <>
                  <MobileNavItem href="/blogs" current={true}>
                    Explore
                  </MobileNavItem>
                  <MobileNavItem href="/my-blogs">
                    My Articles
                  </MobileNavItem>
                  <MobileNavItem href="/create-blog">
                    <PenTool className="mr-2 h-5 w-5" />
                    New Article
                  </MobileNavItem>
                  
                  <button
                    onClick={() => {
                      handleLogout();
                      toggleMobileMenu();
                    }}
                    className="w-full flex items-center px-3 py-2 text-base font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
                  >
                    <LogOut className="mr-2 h-5 w-5" />
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <a 
                    href="/login" 
                    className="block px-3 py-2 rounded-md text-base font-medium text-blue-100 hover:text-white hover:bg-blue-700"
                  >
                    <User className="inline mr-2 h-5 w-5" />
                    Login
                  </a>
                  <a 
                    href="/signup"
                    className="block px-3 py-2 rounded-md text-base font-medium text-white bg-blue-600 hover:bg-blue-700"
                  >
                    Sign Up
                  </a>
                </>
              )}
            </div>
            
            {/* Profile section in mobile menu */}
            {isLoggedIn && (
              <div className="pt-4 pb-3 border-t border-blue-700">
                <div className="flex items-center px-5">
                  <div className="flex-shrink-0">
                    <div className="h-10 w-10 rounded-full bg-blue-200 flex items-center justify-center text-blue-800 font-bold">
                      JD
                    </div>
                  </div>
                  <div className="ml-3">
                    <div className="text-base font-medium text-white">Jane Doe</div>
                    <div className="text-sm font-medium text-blue-200">jane@example.com</div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </nav>
      
      {/* Content placeholder to demonstrate fixed navbar */}
     
    </div>
  );
}

// Desktop Navigation Item
function NavItem({ href, children, current = false }) {
  return (
    <a
      href={href}
      className={`px-3 py-2 text-sm font-medium rounded-md ${
        current
          ? 'bg-blue-700 text-white'
          : 'text-blue-100 hover:bg-blue-700 hover:text-white'
      } transition-colors duration-200`}
    >
      {children}
    </a>
  );
}

// Mobile Navigation Item
function MobileNavItem({ href, children, current = false }) {
  return (
    <a
      href={href}
      className={`flex items-center px-3 py-2 rounded-md text-base font-medium ${
        current
          ? 'bg-blue-700 text-white'
          : 'text-blue-100 hover:bg-blue-700 hover:text-white'
      }`}
    >
      {children}
    </a>
  );
}

export default Navbar;
