import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import api from '../utils/api';

function CreateBlog() {
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('');
  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const categories = ['Career', 'Finance', 'Travel', 'Technology', 'Health', 'Other'];

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !category || !content) {
      toast.error('Please fill in all required fields', {
        className: 'toast-error',
        autoClose: 3000,
      });
      return;
    }
    try {
      setLoading(true);
      await api.post('/blogs', { title, category, content, image });
      toast.success('Blog created successfully!', {
        className: 'toast-success',
        autoClose: 3000,
      });
      navigate('/my-blogs');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to create blog', {
        className: 'toast-error',
        autoClose: 3000,
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h2 className="text-3xl font-bold text-gray-800 mb-6 border-b-2 border-blue-600 pb-2">
        Create Blog
      </h2>
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded-xl shadow-lg space-y-6"
        aria-label="Create blog form"
      >
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
            Title
          </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter blog title"
            className={`w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200 ${
              !title && title !== '' ? 'border-red-500' : 'border-gray-300'
            }`}
            required
            aria-required="true"
            aria-label="Blog title"
            disabled={loading}
          />
          {!title && title !== '' && (
            <p className="text-red-500 text-sm mt-1">Title is required</p>
          )}
        </div>
        <div>
          <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
            Category
          </label>
          <select
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className={`w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200 ${
              !category && category !== '' ? 'border-red-500' : 'border-gray-300'
            }`}
            required
            aria-required="true"
            aria-label="Blog category"
            disabled={loading}
          >
            <option value="">Select Category</option>
            {categories.map((cat) => (
              <option key={cat} value={cat}>
                {cat}
              </option>
            ))}
          </select>
          {!category && category !== '' && (
            <p className="text-red-500 text-sm mt-1">Category is required</p>
          )}
        </div>
        <div>
          <label htmlFor="content" className="block text-sm font-medium text-gray-700 mb-1">
            Content
          </label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Write your blog content"
            className={`w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors duration-200 resize-y h-48 ${
              !content && content !== '' ? 'border-red-500' : 'border-gray-300'
            }`}
            required
            aria-required="true"
            aria-label="Blog content"
            disabled={loading}
          />
          {!content && content !== '' && (
            <p className="text-red-500 text-sm mt-1">Content is required</p>
          )}
        </div>
        <div>
          <label htmlFor="image" className="block text-sm font-medium text-gray-700 mb-1">
            Image (Optional)
          </label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={handleImageChange}
            className="w-full p-2 border border-gray-300 rounded-md file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            aria-label="Upload blog image"
            disabled={loading}
          />
        </div>
        {image && (
          <div className="mb-4">
            <img
              src={image}
              alt="Blog preview"
              className="max-w-full max-h-64 object-contain rounded-md shadow-sm"
            />
          </div>
        )}
        <button
          type="submit"
          className={`w-full flex items-center justify-center p-3 rounded-md text-white transition-colors duration-200 ${
            loading
              ? 'bg-blue-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2'
          }`}
          aria-label={loading ? 'Creating blog' : 'Create blog'}
          disabled={loading}
        >
          {loading ? (
            <>
              <div className="inline-block h-5 w-5 animate-spin rounded-full border-2 border-white border-t-transparent mr-2"></div>
              Creating...
            </>
          ) : (
            'Create Blog'
          )}
        </button>
      </form>
    </div>
  );
}

export default CreateBlog;
