import React from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import api from '../utils/api';

function BlogCard({ blog, isMyBlog, onDelete }) {
  const navigate = useNavigate();

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await api.delete(`/blogs/${blog._id}`);
        toast.success('Blog deleted successfully!');
        onDelete(blog._id);
      } catch (err) {
        toast.error(err.response?.data?.message || 'Failed to delete blog');
      }
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow-md">
      {blog.image && (
        <img src={blog.image} alt={blog.title} className="w-full h-48 object-fit rounded-md mb-4" />
      )}
      <h3 className="text-xl font-bold">{blog.title}</h3>
      <p className="text-gray-600 text-sm mb-2">Category: {blog.category}</p>
      <p className="text-gray-600 text-sm mb-2">Author: {blog.author}</p>
      <p className="text-gray-700 mb-4">{blog.content.substring(0, 100)}...</p>
      {isMyBlog && (
        <div className="flex space-x-2">
          <button
            onClick={() => navigate(`/edit-blog/${blog._id}`)}
            className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
          >
            Edit
          </button>
          <button
            onClick={handleDelete}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Delete
          </button>
        </div>
      )}
    </div>
  );
}

export default BlogCard;
