import React from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import api from '../utils/api';

function BlogCard({ blog, isMyBlog, onDelete }) {
  const navigate = useNavigate();

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this blog?')) {
      try {
        await api.delete(`/blogs/${blog._id}`);
        toast.success('Blog deleted successfully!');
        onDelete(blog._id);
      } catch (err) {
        toast.error(err.response?.data?.message || 'Failed to delete blog');
      }
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-lg overflow-hidden transition-transform duration-200 hover:scale-105 hover:shadow-xl">
      <div className="p-6">
        {blog.image ? (
          <div className="mb-4">
            <img
              src={blog.image}
              alt={blog.title}
              className="max-w-full max-h-48 object-contain rounded-md"
            />
          </div>
        ) : (
          <div className="mb-4 bg-gray-200 h-48 flex items-center justify-center rounded-md">
            <span className="text-gray-500 text-sm">No image available</span>
          </div>
        )}
        <h3 className="text-2xl font-semibold text-gray-800 mb-2 line-clamp-2">{blog.title}</h3>
        <div className="text-sm text-gray-600 mb-3 space-y-1">
          <p>
            <span className="font-medium">Category:</span> {blog.category}
          </p>
          <p>
            <span className="font-medium">Author:</span> {blog.author}
          </p>
          <p>
            <span className="font-medium">Published:</span>{' '}
            {new Date(blog.createdAt).toLocaleDateString()}
          </p>
        </div>
        <p className="text-gray-700 text-base mb-4 line-clamp-3">
          {blog.content || 'No content available'}
        </p>
        {isMyBlog && (
          <div className="flex space-x-3">
            <button
              onClick={() => navigate(`/edit-blog/${blog._id}`)}
              className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors duration-200"
              aria-label={`Edit blog titled ${blog.title}`}
            >
              Edit
            </button>
            <button
              onClick={handleDelete}
              className="flex-1 bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 transition-colors duration-200"
              aria-label={`Delete blog titled ${blog.title}`}
            >
              Delete
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default BlogCard;
