import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../utils/api';
import { toast } from 'react-toastify';

function EditBlog() {
  const { blogId } = useParams();
  const navigate = useNavigate();
  
  const [isLoading, setIsLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [blog, setBlog] = useState({
    title: '',
    content: '',
    category: '',
    coverImage: ''
  });
  
  const [categories] = useState([
    'Technology', 'Business', 'Health', 'Science', 
    'Travel', 'Food', 'Lifestyle', 'Education', 'Other'
  ]);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        setIsLoading(true);
        const res = await api.get(`/blogs/${blogId}`);
        setBlog(res.data.data);
      } catch (err) {
        toast.error(err.response?.data?.message || 'Failed to fetch blog details');
        navigate('/my-blogs');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchBlog();
  }, [blogId, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBlog(prevBlog => ({
      ...prevBlog,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    // This would handle file uploads in a real app
    // For now just store the file name
    if (e.target.files && e.target.files[0]) {
      setBlog(prevBlog => ({
        ...prevBlog,
        coverImage: e.target.files[0].name
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!blog.title || !blog.content || !blog.category) {
      toast.error('Please fill in all required fields');
      return;
    }
    
    try {
      setSubmitting(true);
      await api.put(`/blogs/${blogId}`, blog);
      toast.success('Blog updated successfully!');
      navigate('/my-blogs');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update blog');
      setSubmitting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="bg-gray-50 min-h-screen pt-20 pb-12">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-xl shadow-md overflow-hidden p-6 sm:p-8">
          <div className="border-b border-gray-200 pb-5 mb-6">
            <h1 className="text-2xl font-bold text-gray-900">Edit Blog</h1>
            <p className="mt-2 text-sm text-gray-500">Update your blog post with the form below</p>
          </div>
          
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                Title <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={blog.title}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                placeholder="Enter the blog title"
                required
              />
            </div>

            <div>
              <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                Category <span className="text-red-500">*</span>
              </label>
              <select
                id="category"
                name="category"
                value={blog.category}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                required
              >
                <option value="">Select a category</option>
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label htmlFor="coverImage" className="block text-sm font-medium text-gray-700">
                Cover Image
              </label>
              <div className="mt-1 flex items-center space-x-4">
                {blog.coverImage && (
                  <div className="flex items-center">
                    <span className="text-sm text-gray-500">{blog.coverImage}</span>
                    <button
                      type="button"
                      onClick={() => setBlog({ ...blog, coverImage: '' })}
                      className="ml-2 text-sm text-red-600 hover:text-red-800"
                    >
                      Remove
                    </button>
                  </div>
                )}
                <div className="relative">
                  <input
                    id="coverImage"
                    name="coverImage"
                    type="file"
                    className="sr-only"
                    onChange={handleImageChange}
                  />
                  <label
                    htmlFor="coverImage"
                    className="cursor-pointer rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                  >
                    {blog.coverImage ? 'Change image' : 'Upload image'}
                  </label>
                </div>
              </div>
              <p className="mt-1 text-xs text-gray-500">PNG, JPG or GIF up to 2MB</p>
            </div>

            <div>
              <label htmlFor="content" className="block text-sm font-medium text-gray-700">
                Content <span className="text-red-500">*</span>
              </label>
              <textarea
                id="content"
                name="content"
                rows={12}
                value={blog.content}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm"
                placeholder="Write your blog content here..."
                required
              />
            </div>

            <div className="flex justify-end space-x-3 pt-5">
              <button
                type="button"
                onClick={() => navigate('/my-blogs')}
                className="rounded-md border border-gray-300 bg-white py-2 px-4 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={submitting}
                className={`rounded-md border border-transparent bg-blue-600 py-2 px-4 text-sm font-medium text-white shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 ${
                  submitting ? 'opacity-75 cursor-not-allowed' : ''
                }`}
              >
                {submitting ? (
                  <span className="flex items-center">
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Saving...
                  </span>
                ) : (
                  'Save Changes'
                )}
              </button>
            </div>
          </form>
        </div>
        
        <div className="mt-6 text-center">
          <button 
            onClick={() => navigate('/my-blogs')} 
            className="text-sm text-blue-600 hover:text-blue-800"
          >
            ← Back to My Blogs
          </button>
        </div>
      </div>
    </div>
  );
}

export default EditBlog;
